mapper.select:
/** * Query ID : selectTransSetting * Description : 소스시스템 전송설정 조회 end * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.TRANS_ID , A.KC_ID , B.KC_IP , B.KC_PORT , A.CONNECT_NM , A.SNAPSHOT_MODE , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE SYS_CD = A.SNAPSHOT_MODE ) AS SNAPSHOT_NM , A.DB_ID , (SELECT DB_NM FROM T_DB_I WHERE DB_ID = A.DB_ID) AS DB_NM , A.DB_SVR_ID , (SELECT DB_SVR_NM FROM T_DBSVR_I WHERE DB_SVR_ID = A.DB_SVR_ID) AS DB_SVR_NM , A.EXE_STATUS , A.TRANS_EXRT_TRG_TB_ID , A.TRANS_EXRT_EXCT_TB_ID , A.FRST_REGR_ID , A.FRST_REG_DTM , A.LST_MDFR_ID , A.LST_MDF_DTM , A.COMPRESSION_TYPE , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE SYS_CD = A.COMPRESSION_TYPE ) AS COMPRESSION_NM , A.META_DATA , A.REGI_ID , C.REGI_NM , C.REGI_IP , C.REGI_PORT , A.CONNECT_TYPE FROM T_TRANSCNG_I A LEFT JOIN T_TRANS_CON_INF B ON A.KC_ID = B.KC_ID LEFT JOIN T_TRANS_REGI_INF C ON A.REGI_ID = C.REGI_ID WHERE 1=1 AND A.DB_SVR_ID = #{db_svr_id}
AND A.CONNECT_NM = #{connect_nm}
ORDER BY A.LST_MDF_DTM DESC

/** * Query ID : selectTransDBMS * Description : 전송설정 조회 * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.TRANS_SYS_ID , A.TRANS_SYS_NM , A.DBMS_DSCD_NM , A.DBMS_DSCD , A.IPADR , A.DTB_NM , A.SPR_USR_ID , A.PORTNO , A.SCM_NM , A.PWD , A.FRST_REGR_ID , A.FRST_REG_DTM , A.LST_MDFR_ID , A.LST_MDF_DTM , A.EXE_STATUS FROM ( SELECT TRANS_SYS_ID , TRANS_SYS_NM , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE SYS_CD = DBMS_DSCD ) AS DBMS_DSCD_NM , DBMS_DSCD , IPADR , DTB_NM , SPR_USR_ID , PORTNO , SCM_NM , PWD , FRST_REGR_ID , TO_CHAR(FRST_REG_DTM,'YYYY-MM-DD HH24:MI:SS') AS FRST_REG_DTM , LST_MDFR_ID , TO_CHAR(LST_MDF_DTM,'YYYY-MM-DD HH24:MI:SS') AS LST_MDF_DTM , EXE_STATUS FROM T_TRANS_SYS_INF WHERE 1 = 1
AND TRANS_SYS_ID = #{trans_sys_id}
AND TRANS_SYS_NM LIKE '%' || #{trans_sys_nm} || '%'
AND IPADR LIKE '%' || #{ipadr} || '%'
AND DTB_NM = #{dtb_nm}
AND EXE_STATUS = 'TC001501'
ORDER BY LST_MDF_DTM DESC ) A

mapper.insert:
		/**
		* Query ID : insertTransDBMS
		* Description : TRANS DBMS 시스템 등록
		* Author : 변승우
		**/
        INSERT INTO T_TRANS_SYS_INF
                    (
                     TRANS_SYS_ID
                   , TRANS_SYS_NM
                   , DBMS_DSCD
                   , IPADR
                   , DTB_NM
                   , SPR_USR_ID
                   , PORTNO
                   , SCM_NM
                   , PWD
                   , FRST_REGR_ID
                   , FRST_REG_DTM
                   , LST_MDFR_ID
                   , LST_MDF_DTM
                   , EXE_STATUS
                    ) 
                    VALUES
                    (
                     nextval('q_trans_sys_inf_01')
                   , #{trans_sys_nm}
                   , #{dbms_dscd}
                   , #{ipadr}
                   , #{dtb_nm}
                   , #{spr_usr_id}
                   , #{portno}
                   , #{scm_nm}
                   , #{pwd}
                   , #{frst_regr_id}
                   , clock_timestamp()
                   , #{lst_mdfr_id}
                   , clock_timestamp()	
                   , #{exe_status}
                    )

mapper.update:
		/**
	 	* Query ID : updateTransDBMS
	 	* Description : TRANS DBMS 수정
	 	* Author : 
	 	**/
        UPDATE T_TRANS_SYS_INF
           SET
               IPADR = #{ipadr}
             , DTB_NM = #{dtb_nm}
             , SPR_USR_ID = #{spr_usr_id}
             , PORTNO = #{portno}
             , SCM_NM = #{scm_nm}
             , PWD = #{pwd}
             , EXE_STATUS = #{exe_status}
             , LST_MDFR_ID = #{lst_mdfr_id}
             , LST_MDF_DTM = clock_timestamp()
         WHERE TRANS_SYS_ID=#{trans_sys_id}::numeric

mapper.select:
/** * Query ID : selectTargetTransSetting end * Description : 타겟시스템 전송설정 조회 * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.TRANS_ID , C.KC_IP , C.KC_PORT , A.KC_ID , A.CONNECT_NM , A.SNAPSHOT_MODE , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE SYS_CD = A.SNAPSHOT_MODE ) AS SNAPSHOT_NM , A.DB_SVR_ID , (SELECT DB_SVR_NM FROM T_DBSVR_I WHERE DB_SVR_ID = A.DB_SVR_ID) AS DB_SVR_NM , A.EXE_STATUS , A.TRANS_EXRT_TRG_TB_ID , A.TRANS_EXRT_EXCT_TB_ID , A.FRST_REGR_ID , A.FRST_REG_DTM , A.LST_MDFR_ID , A.LST_MDF_DTM , A.TRANS_SYS_ID , B.TRANS_SYS_NM , B.EXE_STATUS AS DB_EXE_STATUS , A.REGI_ID , A.TOPIC_TYPE , D.REGI_NM , D.REGI_IP , D.REGI_PORT FROM T_TRANSCNG_TARGET_I A LEFT OUTER JOIN T_TRANS_SYS_INF B ON A.TRANS_SYS_ID = B.TRANS_SYS_ID LEFT JOIN T_TRANS_CON_INF C ON A.KC_ID = C.KC_ID LEFT JOIN T_TRANS_REGI_INF D ON A.REGI_ID = D.REGI_ID WHERE 1=1 AND A.DB_SVR_ID = #{db_svr_id}
AND A.CONNECT_NM = #{connect_nm}
ORDER BY A.LST_MDF_DTM DESC

mapper.insert:
		/**
		* Query ID : insertConnectInfo
		* Description : 전송설정 정보 등록 end
		* Author : 변승우
		**/
        INSERT INTO T_TRANSCNG_I
                   (
                    TRANS_ID
                  , CONNECT_NM
                  , SNAPSHOT_MODE
                  , DB_ID
                  , DB_SVR_ID
                  , TRANS_EXRT_TRG_TB_ID
                  , FRST_REGR_ID
                  , FRST_REG_DTM
                  , LST_MDFR_ID
                  , LST_MDF_DTM 
                  , COMPRESSION_TYPE
                  , META_DATA
                  , KC_ID
                  , TRANS_COM_ID
                  , REGI_ID
                  , CONNECT_TYPE
                   ) 
            VALUES
                   (
                    nextval('q_t_transcng_i_01')
                  , #{connect_nm}
                  , #{snapshot_mode}
                  , #{db_id}
                  , #{db_svr_id}
                  , #{trans_exrt_trg_tb_id}
                  , #{frst_regr_id}
                  , clock_timestamp()
                  , #{frst_regr_id}
                  , clock_timestamp()
                  , #{compression_type}
                  , #{meta_data}
                  , #{kc_id}::numeric
                  , #{trans_com_id}::numeric
                  , #{regi_id}::numeric
                  , #{connect_type}
                   )

mapper.update:
/**
* Query ID : "updateTransCommonSetting"
* Description : 기본설정 수정 end
* Author : 
**/
UPDATE T_TRANSCOMCNG_I
   SET PLUGIN_NAME = #{plugin_name}
     , TRANS_COM_CNG_NM = #{trans_com_cng_nm}
     , HEARTBEAT_INTERVAL_MS = #{heartbeat_interval_ms}
     , HEARTBEAT_ACTION_QUERY = #{heartbeat_action_query}
     , MAX_BATCH_SIZE = #{max_batch_size}
     , MAX_QUEUE_SIZE = #{max_queue_size}
     , OFFSET_FLUSH_INTERVAL_MS = #{offset_flush_interval_ms}
     , OFFSET_FLUSH_TIMEOUT_MS = #{offset_flush_timeout_ms}
     , AUTO_CREATE = #{auto_create}
     , TRANSFORMS_YN = #{transforms_yn}
     , LST_MDFR_ID = #{lst_mdfr_id}
     , LST_MDF_DTM = clock_timestamp()
 WHERE TRANS_COM_ID = #{trans_com_id}::numeric
